<script setup>
import { ref } from 'vue';
const themeList = [
    {
        theme: "light",
        show: ref(true),
    },
    {
        theme: "dark",
        show: ref(false),
    }
];
let index = 0;
const theme = ref(themeList[index].theme);
const changeTheme = () => {
    index++;
    if (index >= themeList.length) { index = 0 }
    for (const t of themeList) { t.show.value = false }
    
    themeList[index].show.value = true;
    theme.value = themeList[index].theme;
    document.documentElement.setAttribute("data-theme", theme.value);
}
</script>
<style lang="scss" scoped>
.theme {
    cursor: pointer;
}
</style>
